                sp_3072_mont_mul_48(t[29], t[15], t[14], m, mp);
                sp_3072_mont_sqr_48(t[30], t[15], m, mp);
                sp_3072_mont_mul_48(t[31], t[16], t[15], m, mp);

                i = (bits - 1) / 32;
                n = e[i--];
                c = bits & 31;
