                    sp_3072_mont_sqr_48(r, r, m, mp);
                    sp_3072_mont_sqr_48(r, r, m, mp);
                    sp_3072_mont_sqr_48(r, r, m, mp);
                    sp_3072_mont_sqr_48(r, r, m, mp);
