        static void sp_4096_mont_norm_128(sp_digit* r, const sp_digit* m)
        {
            XMEMSET(r, 0, sizeof(sp_digit) * 128);

            /* r = 2^n mod m */
            sp_4096_sub_in_place_128(r, m);
        }

