    /* Read big endian unsigned byte array into r.
     *
     * r  A single precision integer.
     * size  Maximum number of bytes to convert
     * a  Byte array.
     * n  Number of bytes in array to read.
     */
    static void sp_2048_from_bin(sp_digit* r, int size, const byte* a, int n)
    {
        int i, j;
        byte* d;

        for (i = n - 1,j = 0; i >= 7; i -= 8) {
            r[j]  = ((sp_digit)a[i - 0] <<  0) |
                    ((sp_digit)a[i - 1] <<  8) |
                    ((sp_digit)a[i - 2] << 16) |
                    ((sp_digit)a[i - 3] << 24) |
                    ((sp_digit)a[i - 4] << 32) |
                    ((sp_digit)a[i - 5] << 40) |
                    ((sp_digit)a[i - 6] << 48) |
                    ((sp_digit)a[i - 7] << 56);
            j++;
        }

        if (i >= 0) {
            r[j] = 0;

            d = (byte*)r;
            switch (i) {
                case 6: d[n - 1 - 6] = a[6]; //fallthrough
                case 5: d[n - 1 - 5] = a[5]; //fallthrough
                case 4: d[n - 1 - 4] = a[4]; //fallthrough
                case 3: d[n - 1 - 3] = a[3]; //fallthrough
                case 2: d[n - 1 - 2] = a[2]; //fallthrough
                case 1: d[n - 1 - 1] = a[1]; //fallthrough
                case 0: d[n - 1 - 0] = a[0]; //fallthrough
            }
            j++;
        }

        for (; j < size; j++) {
            r[j] = 0;
        }
    }

    /* Convert an mp_int to an array of sp_digit.
     *
     * r  A single precision integer.
     * size  Maximum number of bytes to convert
     * a  A multi-precision integer.
     */
    static void sp_2048_from_mp(sp_digit* r, int size, const mp_int* a)
    {
    #if DIGIT_BIT == 64
        int j;

        XMEMCPY(r, a->dp, sizeof(sp_digit) * a->used);

        for (j = a->used; j < size; j++) {
            r[j] = 0;
        }
    #elif DIGIT_BIT > 64
        int i, j = 0;
        word32 s = 0;

        r[0] = 0;
        for (i = 0; i < a->used && j < size; i++) {
            r[j] |= ((sp_digit)a->dp[i] << s);
            r[j] &= 0xffffffffffffffffl;
            s = 64U - s;
            if (j + 1 >= size) {
                break;
            }
            /* lint allow cast of mismatch word32 and mp_digit */
            r[++j] = (sp_digit)(a->dp[i] >> s); /*lint !e9033*/
            while ((s + 64U) <= (word32)DIGIT_BIT) {
                s += 64U;
                r[j] &= 0xffffffffffffffffl;
                if (j + 1 >= size) {
                    break;
                }
                if (s < (word32)DIGIT_BIT) {
                    /* lint allow cast of mismatch word32 and mp_digit */
                    r[++j] = (sp_digit)(a->dp[i] >> s); /*lint !e9033*/
                }
                else {
                    r[++j] = 0L;
                }
            }
            s = (word32)DIGIT_BIT - s;
        }

        for (j++; j < size; j++) {
            r[j] = 0;
        }
    #else
        int i, j = 0, s = 0;

        r[0] = 0;
        for (i = 0; i < a->used && j < size; i++) {
            r[j] |= ((sp_digit)a->dp[i]) << s;
            if (s + DIGIT_BIT >= 64) {
                r[j] &= 0xffffffffffffffffl;
                if (j + 1 >= size) {
                    break;
                }
                s = 64 - s;
                if (s == DIGIT_BIT) {
                    r[++j] = 0;
                    s = 0;
                }
                else {
                    r[++j] = a->dp[i] >> s;
                    s = DIGIT_BIT - s;
                }
            }
            else {
                s += DIGIT_BIT;
            }
        }

        for (j++; j < size; j++) {
            r[j] = 0;
        }
    #endif
    }

    /* Write r as big endian to byte array.
     * Fixed length number of bytes written: 256
     *
     * r  A single precision integer.
     * a  Byte array.
     */
    static void sp_2048_to_bin(sp_digit* r, byte* a)
    {
        int i, j;

        for (i = 31, j = 0; i >= 0; i--) {
            a[j++] = r[i] >> 56;
            a[j++] = r[i] >> 48;
            a[j++] = r[i] >> 40;
            a[j++] = r[i] >> 32;
            a[j++] = r[i] >> 24;
            a[j++] = r[i] >> 16;
            a[j++] = r[i] >> 8;
            a[j++] = r[i] >> 0;
        }
    }

