        u += sp_2048_sub_in_place_16(z1, z2);
        u += sp_2048_sub_in_place_16(z1, z0);
        u += sp_2048_add_16(r + 8, r + 8, z1);
        u += sp_2048_add_8(r + 16, r + 16, z2);
        sp_2048_add_zero_8(r + 24, z2 + 8, u);
