    static void sp_2048_mont_norm_16(sp_digit* r, const sp_digit* m)
    {
        XMEMSET(r, 0, sizeof(sp_digit) * 16);

        /* r = 2^n mod m */
        sp_2048_sub_in_place_16(r, m);
    }

