    /* Square the Montgomery form number. (r = a * a mod m)
     *
     * r   Result of squaring.
     * a   Number to square in Montogmery form.
     * m   Modulus (prime).
     * mp  Montogmery mulitplier.
     */
    static void sp_2048_mont_sqr_32(sp_digit* r, const sp_digit* a, const sp_digit* m,
            sp_digit mp)
    {
        sp_2048_sqr_32(r, a);
        sp_2048_mont_reduce_32(r, m, mp);
    }

    /* Divide the double width number (d1|d0) by the dividend. (d1|d0 / div)
     *
     * d1   The high order half of the number to divide.
     * d0   The low order half of the number to divide.
     * div  The dividend.
     * returns the result of the division.
     */
    static sp_digit div_2048_word_32(sp_digit d1, sp_digit d0, sp_digit div)
    {
        sp_digit r;

        __asm__ __volatile__ (
            "lsr	x5, %[div], 32\n\t"
            "add	x5, x5, 1\n\t"

            "udiv	x3, %[d1], x5\n\t"
            "lsl	x6, x3, 32\n\t"
            "mul	x4, %[div], x6\n\t"
            "umulh	x3, %[div], x6\n\t"
            "subs	%[d0], %[d0], x4\n\t"
            "sbc	%[d1], %[d1], x3\n\t"

            "udiv	x3, %[d1], x5\n\t"
            "lsl	x3, x3, 32\n\t"
            "add	x6, x6, x3\n\t"
            "mul	x4, %[div], x3\n\t"
            "umulh	x3, %[div], x3\n\t"
            "subs	%[d0], %[d0], x4\n\t"
            "sbc	%[d1], %[d1], x3\n\t"

            "lsr	x3, %[d0], 32\n\t"
            "orr	x3, x3, %[d1], lsl 32\n\t"

            "udiv	x3, x3, x5\n\t"
            "add	x6, x6, x3\n\t"
            "mul	x4, %[div], x3\n\t"
            "umulh	x3, %[div], x3\n\t"
            "subs	%[d0], %[d0], x4\n\t"
            "sbc	%[d1], %[d1], x3\n\t"

            "lsr	x3, %[d0], 32\n\t"
            "orr	x3, x3, %[d1], lsl 32\n\t"

            "udiv	x3, x3, x5\n\t"
            "add	x6, x6, x3\n\t"
            "mul	x4, %[div], x3\n\t"
            "sub	%[d0], %[d0], x4\n\t"

            "udiv	x3, %[d0], %[div]\n\t"
            "add	%[r], x6, x3\n\t"

            : [r] "=r" (r)
            : [d1] "r" (d1), [d0] "r" (d0), [div] "r" (div)
            : "x3", "x4", "x5", "x6"
        );

        return r;
    }

    /* AND m into each word of a and store in r.
     *
     * r  A single precision integer.
     * a  A single precision integer.
     * m  Mask to AND against each digit.
     */
    static void sp_2048_mask_32(sp_digit* r, const sp_digit* a, sp_digit m)
    {
    #ifdef WOLFSSL_SP_SMALL
        int i;

        for (i=0; i<32; i++) {
            r[i] = a[i] & m;
        }
    #else
        int i;

        for (i = 0; i < 32; i += 8) {
            r[i+0] = a[i+0] & m;
            r[i+1] = a[i+1] & m;
            r[i+2] = a[i+2] & m;
            r[i+3] = a[i+3] & m;
            r[i+4] = a[i+4] & m;
            r[i+5] = a[i+5] & m;
            r[i+6] = a[i+6] & m;
            r[i+7] = a[i+7] & m;
        }
    #endif
    }

